import React, { useEffect, useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  ScrollView,
  Alert,
} from 'react-native';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { FIREBASE_DB } from '../../FirebaseCongig';

interface Props {
  platId: string;
  platNom: string;
  onBack: () => void;
  onAddToCart: () => void;
}

interface Ingredient {
  id: string;
  nom: string;
  quantite: number;
}

const PlatDetails: React.FC<Props> = ({ platId, platNom, onBack, onAddToCart }) => {
  const [ingredients, setIngredients] = useState<Ingredient[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadIngredients();
  }, [platId]);

  const loadIngredients = async () => {
    try {
      console.log('ID du plat:', platId);

      // Récupérer les ingrédients
      const ingredientsSnapshot = await getDocs(collection(FIREBASE_DB, 'ingredients'));
      console.log('=== Ingrédients disponibles ===');
      ingredientsSnapshot.forEach(doc => {
        console.log(`ID: ${doc.id} - Nom: ${doc.data().nom}`);
      });

      // Récupérer les relations plat-ingrédients
      const platIngredientsRef = collection(FIREBASE_DB, 'plat_ingredients');
      console.log('Recherche des relations pour le plat:', platId);

      // Afficher toutes les relations pour débugger
      const toutesLesRelations = await getDocs(platIngredientsRef);
      console.log('Toutes les relations dans la collection:');
      toutesLesRelations.forEach(doc => {
        console.log('Relation trouvée:', doc.data());
      });

      // Modifier la requête pour utiliser id_plats au lieu de id_plat
      const q = query(platIngredientsRef, where('id_plats', '==', Number(platId)));
      const querySnapshot = await getDocs(q);

      console.log('=== Relations plat-ingrédients trouvées pour ce plat ===');
      querySnapshot.forEach(doc => {
        console.log('Relation:', doc.data());
      });

      const ingredientsPromises = querySnapshot.docs.map(async (doc) => {
        const platIngredient = doc.data();
        console.log('Traitement de la relation:', platIngredient);

        // Convertir l'id_ingredient en string s'il ne l'est pas déjà
        const ingredientId = String(platIngredient.id_ingredient);

        // Récupérer directement l'ingrédient par son ID
        const ingredientDocs = ingredientsSnapshot.docs;
        const ingredient = ingredientDocs.find(ingDoc => ingDoc.id === ingredientId);

        if (ingredient) {
          console.log('Ingrédient trouvé:', ingredient.data());
          const ingredientData = ingredient.data();
          return {
            id: ingredient.id,
            nom: ingredientData.nom,
            quantite: Number(platIngredient.quantite)
          };
        }
        console.log('Ingrédient non trouvé pour id:', ingredientId);
        return null;
      });

      const ingredientsData = (await Promise.all(ingredientsPromises)).filter(
        (ingredient): ingredient is Ingredient => ingredient !== null
      );

      console.log('Ingrédients finaux trouvés:', ingredientsData);
      setIngredients(ingredientsData);
    } catch (error) {
      console.error('Erreur lors du chargement des ingrédients:', error);
      Alert.alert('Erreur', 'Impossible de charger les ingrédients');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={onBack}>
          <Text style={styles.backButton}>← Retour</Text>
        </TouchableOpacity>
        <Text style={styles.headerTitle}>{platNom}</Text>
        <View style={{ width: 50 }} />
      </View>

      <ScrollView style={styles.content}>
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Ingrédients</Text>
          {isLoading ? (
            <Text style={styles.loadingText}>Chargement des ingrédients...</Text>
          ) : ingredients.length === 0 ? (
            <Text style={styles.emptyText}>Aucun ingrédient listé</Text>
          ) : (
            ingredients.map((ingredient) => (
              <View key={ingredient.id} style={styles.ingredientItem}>
                <Text style={styles.ingredientName}>{ingredient.nom}</Text>
                <Text style={styles.ingredientQuantity}>
                  {ingredient.quantite}
                </Text>
              </View>
            ))
          )}
        </View>
      </ScrollView>

      <TouchableOpacity style={styles.addButton} onPress={onAddToCart}>
        <Text style={styles.addButtonText}>Ajouter au panier</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 16,
    backgroundColor: '#42b983',
  },
  headerTitle: {
    color: 'white',
    fontSize: 18,
    fontWeight: 'bold',
  },
  backButton: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  content: {
    flex: 1,
    padding: 16,
  },
  section: {
    backgroundColor: 'white',
    borderRadius: 8,
    padding: 16,
    marginBottom: 16,
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 16,
    color: '#2c3e50',
  },
  loadingText: {
    textAlign: 'center',
    color: '#666',
    marginTop: 8,
  },
  emptyText: {
    textAlign: 'center',
    color: '#666',
    marginTop: 8,
  },
  ingredientItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 8,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  ingredientName: {
    fontSize: 16,
    color: '#2c3e50',
  },
  ingredientQuantity: {
    fontSize: 14,
    color: '#666',
  },
  addButton: {
    backgroundColor: '#42b983',
    margin: 16,
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
  },
  addButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default PlatDetails;
