import React, { useEffect, useState } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  FlatList, 
  TouchableOpacity,
  Alert
} from 'react-native';
import { collection, getDocs, addDoc } from 'firebase/firestore';
import { FIREBASE_DB, FIREBASE_AUTH } from '../../FirebaseCongig';
import { signOut } from 'firebase/auth';

interface Props {
  onCartPress: () => void;
  onPlatSelect: (plat: { id: string; nom: string }) => void;
}

interface Plat {
  id: string;
  nom: string;
  tps_cuisson: number;
  prix: number;
}

const Plats: React.FC<Props> = ({ onCartPress, onPlatSelect }) => {
  const [plats, setPlats] = useState<Plat[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const user = FIREBASE_AUTH.currentUser;

  useEffect(() => {
    loadPlats();
  }, []);

  const loadPlats = async () => {
    try {
      const platsRef = collection(FIREBASE_DB, 'plats');
      const querySnapshot = await getDocs(platsRef);

      const platsData: Plat[] = [];
      querySnapshot.forEach((doc) => {
        platsData.push({ 
          id: doc.id, 
          ...doc.data() 
        } as Plat);
      });

      setPlats(platsData);
    } catch (error) {
      Alert.alert('Erreur', 'Impossible de charger les plats');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(FIREBASE_AUTH);
    } catch (error) {
      Alert.alert('Erreur', 'Impossible de se déconnecter');
    }
  };

  const addToCart = async (plat: Plat) => {
    if (!user) {
      Alert.alert('Erreur', 'Vous devez être connecté pour ajouter au panier');
      return;
    }

    try {
      const cartRef = collection(FIREBASE_DB, 'panier');
      await addDoc(cartRef, {
        ...plat,
        userId: user.uid,
        dateAdded: new Date()
      });
      Alert.alert('Succès', 'Plat ajouté au panier');
    } catch (error) {
      Alert.alert('Erreur', 'Impossible d\'ajouter au panier');
    }
  };

  const renderPlat = ({ item }: { item: Plat }) => (
    <TouchableOpacity 
      style={styles.platCard}
      onPress={() => onPlatSelect({ id: item.id, nom: item.nom })}
    >
      <Text style={styles.platName}>{item.nom}</Text>
      <Text style={styles.platInfo}>
        Temps de cuisson: {item.tps_cuisson} min
      </Text>
      <Text style={styles.platPrice}>{item.prix.toFixed(2)} €</Text>
      <TouchableOpacity 
        style={styles.addButton}
        onPress={(e) => {
          e.stopPropagation(); // Empêche la navigation vers les détails
          addToCart(item);
        }}
      >
        <Text style={styles.buttonText}>Ajouter au panier</Text>
      </TouchableOpacity>
    </TouchableOpacity>
  );

  if (isLoading) {
    return (
      <View style={styles.centerContainer}>
        <Text>Chargement des plats...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.welcomeText}>Bienvenue {user?.email}</Text>
        <TouchableOpacity 
          style={styles.logoutButton}
          onPress={handleLogout}
        >
          <Text style={styles.buttonText}>Déconnexion</Text>
        </TouchableOpacity>
      </View>

      {plats.length === 0 ? (
        <View style={styles.centerContainer}>
          <Text>Aucun plat disponible</Text>
        </View>
      ) : (
        <View style={styles.content}>
          <FlatList
            data={plats}
            renderItem={renderPlat}
            keyExtractor={(item) => item.id}
            contentContainerStyle={styles.listContainer}
          />
          <TouchableOpacity 
            style={styles.cartButton}
            onPress={onCartPress}
          >
            <Text style={styles.cartButtonText}>Voir mon panier</Text>
          </TouchableOpacity>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5',
  },
  content: {
    flex: 1,
  },
  header: {
    padding: 16,
    backgroundColor: '#42b983',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  welcomeText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  logoutButton: {
    backgroundColor: '#2c3e50',
    padding: 8,
    borderRadius: 6,
  },
  centerContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
  },
  listContainer: {
    padding: 16,
    paddingBottom: 80,
  },
  platCard: {
    backgroundColor: 'white',
    padding: 16,
    borderRadius: 8,
    marginBottom: 16,
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  platName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  platInfo: {
    color: '#666',
    marginBottom: 8,
  },
  platPrice: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#2c3e50',
    marginBottom: 12,
  },
  addButton: {
    backgroundColor: '#42b983',
    padding: 10,
    borderRadius: 6,
    alignItems: 'center',
  },
  buttonText: {
    color: 'white',
    fontWeight: 'bold',
  },
  cartButton: {
    position: 'absolute',
    bottom: 16,
    left: 16,
    right: 16,
    backgroundColor: '#2c3e50',
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
  },
  cartButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default Plats;
